'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:vline' );


// VERTICAL LINE //

/**
* Returns a function for creating vertical lines.
*
* @private
* @returns {Function} function to create vertical lines
*/
function vline() {
	/*jshint validthis:true */ // TODO: eslint
	var height = this.state.graphHeight();
	var xPos = this.state.xPos();

	/**
	* Creates a vertical line as an SVG path.
	*
	* @private
	* @param {Array} d - datum
	* @param {number} i - datum index
	* @returns {string} SVG path string
	*/
	return function vline( d ) {
		var p2;
		var p1;
		var x;
		var l;

		x = xPos( d );
		debug( 'x (px): %d.', x );

		p1 = x + ',' + height;
		p2 = x + ',0';
		l = 'M' + p1 + 'L' + p2;
		debug( 'Line: %s.', l );

		return l;
	}; // end FUNCTION vline()
} // end FUNCTION vline()


// EXPORTS //

module.exports = vline;
