'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:vline' );


// VERTICAL LINE //

/**
* Creates a vertical line as an SVG path.
*
* @private
* @param {Array} d - datum
* @param {number} i - datum index
* @returns {string} SVG path string
*/
function vline( d ) {
	/* jshint validthis: true */ // TODO eslint
	var p2;
	var p1;
	var h;
	var x;
	var l;

	h = this.graphHeight();
	debug( 'Graph height: %d.', h );

	x = this.xPos( d );
	debug( 'x (px): %d.', x );

	p1 = x + ',' + h;
	p2 = x + ',0';
	l = 'M' + p1 + 'L' + p2;
	debug( 'Line: %s.', l );

	return l;
} // end FUNCTION vline()


// EXPORTS //

module.exports = vline;
