'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var getMin = require( './../utils/min.js' );


// X-AXIS MIN //

/**
* Computes the minimum value of the x-axis domain.
*
* @private
* @returns {number} minimum value
*/
function xMin() {
	/* jshint validthis: true */ // TODO: eslint
	var min;
	if ( isNull( this.state.xMin ) ) {
		min = getMin( this.state.x );
		return ( isNull( min ) ) ? 0.0 : min;
	}
	return this.state.xMin;
} // end FUNCTION xMin()


// EXPORTS //

module.exports = xMin;
