'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:xpos' );


// X POSITION //

/**
* Maps a value to a pixel value.
*
* @private
* @param {number} d - datum
* @returns {number} pixel value
*/
function xPos( d ) {
	/* jshint validthis: true */ // TODO: eslint
	var px = this.xScale( d );
	debug( 'Value: %d => Pixel: %d.', d, px );
	return px;
} // end FUNCTION xPos()


// EXPORTS //

module.exports = xPos;
