'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:engine:ypos' );


// Y POSITION //

/**
* Maps a value to a pixel value.
*
* @private
* @param {number} d - datum
* @returns {number} pixel value
*/
function yPos( d ) {
	/* jshint validthis: true */
	var px = this.yScale( d );
	debug( 'Value: %d => Pixel: %d.', d, px );
	return px;
} // end FUNCTION yPos()


// EXPORTS //

module.exports = yPos;
