'use strict';

// MODULES //

var Axis = require( '@stdlib/plot/axis' );


// X AXIS //

/**
* Returns an x-axis.
*
* @private
* @returns {Axis} axis instance
*/
function xAxis() {
	/* jshint validthis:true */ // TODO: eslint
	var axis = new Axis({
		'scale': this.xScale(),
		'tickFormat': this.xTickFormat,
		'numTicks': this.xNumTicks,
		'orientation': this.xAxisOrient,
	});
	return axis;
} // end FUNCTION xAxis()


// EXPORTS //

module.exports = xAxis;
