'use strict';

// MODULES //

var time = require( 'd3-scale' ).scaleTime; // TODO: remove


// X SCALE //

/**
* Returns a scale function for mapping values to a coordinate along the x-axis.
*
* @private
* @returns {Function} scale function
*/
function xScale() {
	/* jshint validthis: true */ // TODO: eslint
	var scale = time().domain( this.xDomain() ).range( this.xRange() );
	return scale;
} // end FUNCTION xScale()


// EXPORTS //

module.exports = xScale;
