'use strict';

// MODULES //

var Axis = require( '@stdlib/plot/axis' );


// Y AXIS //

/**
* Returns a y-axis.
*
* @private
* @returns {Axis} axis instance
*/
function yAxis() {
	/* jshint validthis:true */ // TODO: eslint
	var axis = new Axis({
		'scale': this.yScale(),
		'tickFormat': this.yTickFormat,
		'numTicks': this.yNumTicks,
		'orientation': this.yAxisOrient,
	});
	return axis;
} // end FUNCTION yAxis()


// EXPORTS //

module.exports = yAxis;
