'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:avalue' );
var events = require( './../../events' );
var isValid = require( './../../validators/a_value.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'aValue' );


// SET //

/**
* Sets the annotation accessor.
*
* @private
* @param {Function} fcn - accessor
* @throws {TypeError} must be a function
*/
function set( fcn ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( fcn );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._aValue );

	this._aValue = fcn;
	debug( 'New Value: %s.', this._aValue );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
