'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:labels' );
var copy = require( '@stdlib/utils/copy' );
var events = require( './../../events' );
var isValid = require( './../../validate/labels.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'labels' );


// SET //

/**
* Sets the plot time series labels.
*
* @private
* @param {(StringArray|EmptyArray)} labels - time series labels
* @throws {TypeError} must be either an array of strings or an empty array
*/
function set( labels ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( labels );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', JSON.stringify( this._labels ) );

	this._labels = copy( labels );
	debug( 'New Value: %s.', JSON.stringify( this._labels ) );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
