'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:tension' );
var events = require( './../../events' );
var isValid = require( './../../validators/tension.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'tension' );


// SET //

/**
* Sets the spline interpolation tension.
*
* @private
* @param {number} tension - spline interpolation tension
* @throws {TypeError} must be a number primitive or null
*/
function set( tension ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( tension );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %d.', this._tension );

	this._tension = tension;
	debug( 'New value: %d.', this._tension );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
