'use strict';

/**
* Throws an error as the x-axis scale is fixed and cannot be set.
*
* @private
* @throws {Error} cannot set x-axis scale
*/
function set() {
	/*jshint validthis: true */ // TODO: eslint
	throw new Error( 'invalid operation. Cannot set x-axis scale. To set the x-axis scale, use a line plot.' );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
