'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xlabel' );
var events = require( './../../events' );
var isValid = require( './../../validators/xlabel.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'xLabel' );


// SET //

/**
* Sets the x-axis label.
*
* @private
* @param {string} label - axis label
* @throws {TypeError} must be a string primitive
*/
function set( label ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( label );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._xLabel );

	this._xLabel = label;
	debug( 'New value: %s.', this._xLabel );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
