'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var getMax = require( './../../utils/max.js' );


// GET //

/**
* Returns the maximum value of the x-axis domain.
*
* @private
* @returns {number} maximum value
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var max;
	if ( isNull( this._xMax ) ) {
		max = getMax( this._xData );
		return ( isNull( max ) ) ? 1.0 : max;
	}
	return this._xMax;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
