'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xmax' );
var isNumber = require( '@stdlib/utils/is-number' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );
var isnan = require( '@stdlib/utils/is-nan' ).isPrimitive;
var events = require( './../../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'xMax' );


// SET //

/**
* Sets the maximum value of the x-axis domain.
*
* @private
* @param {(Date|FiniteNumber|null)} min - maximum value
* @throws {TypeError} must be a finite number primitive or null
*/
function set( max ) {
	/*jshint validthis: true */ // TODO: eslint

	// FIXME: check for finite number primitive
	// FIXME: allow Date objects
	if ( !isNumber( max ) && !isNull( max ) ) {
		throw new TypeError( 'invalid value. `xMax` must be a finite number primitive or null. Value: `' + max + '`.' );
	}
	if ( !isNull( max ) ) {
		max = new Date( max ).getTime();
		if ( isnan( max ) ) {
			throw new TypeError( 'invalid value. `xMax` must evaluate to a valid `Date` object. Value: `' + max + '`.' );
		}
	}
	debug( 'Current value: %s.', this._xMax );

	this._xMax = max;
	debug( 'New value: %s.', this._xMax );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
