'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xmax' );
var events = require( './../../events' );
var isValid = require( './../../validators/xmax.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'xMax' );


// SET //

/**
* Sets the maximum value of the x-axis domain.
*
* @private
* @param {(Date|FiniteNumber|null)} min - maximum value
* @throws {TypeError} must be a finite number primitive or null
*/
function set( max ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( max );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._xMax );

	this._xMax = max;
	debug( 'New value: %s.', this._xMax );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
