'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var getMin = require( './../../utils/min.js' );


// GET //

/**
* Returns the minimum value of the x-axis domain.
*
* @private
* @returns {number} minimum value
*/
function get() {
	/* jshint validthis: true */ // TODO: eslint
	var min;
	if ( isNull( this._xMin ) ) {
		min = getMin( this._xData );
		return ( isNull( min ) ) ? 0.0 : min;
	}
	return this._xMin;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
