'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:xtickformat' );
var events = require( './../../events' );
var isValid = require( './../../validate/xtickformat.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'xTickFormat' );


// SET //

/**
* Sets the x-axis tick format.
*
* @private
* @param {string} fmt - axis tick format
* @throws {TypeError} must be a string primitive
*/
function set( fmt ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( fmt );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._xAxis.tickFormat );

	this._xAxis.tickFormat = fmt;
	debug( 'New value: %s.', this._xAxis.tickFormat );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
