'use strict';

// MODULES //

var debug = require( 'debug' )( 'time-series:set:ymax' );
var events = require( './../../events' );
var isValid = require( './../../validators/ymax.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'yMax' );


// SET //

/**
* Sets the maximum value of the y-axis domain.
*
* @private
* @param {(FiniteNumber|null)} min - maximum value
* @throws {TypeError} must be a finite number primitive or null
*/
function set( max ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( max );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %d.', this._yMax );

	this._yMax = max;
	debug( 'New value: %d.', this._yMax );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
