'use strict';

// MODULES //

var isEmptyArray = require( '@stdlib/utils/is-empty-array' );
var isNumberArray = require( '@stdlib/utils/is-number' ).isPrimitiveNumberArray;
var isTypedArray = require( '@stdlib/utils/is-typed-array' );


/**
* Validates `x`.
*
* @private
* @param {*} x - value to test
* @returns {(Error|null)} error object or null
*/
function test( x ) {
	// FIXME: allow date array
	if (
		!isEmptyArray( x ) &&
		!isTypedArray( x ) &&
		!isNumberArray( x )
	) {
		return new TypeError( 'invalid value. `x` must be either a numeric or empty array. Value: `' + x + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
