'use strict';

// MODULES //

var tape = require( 'tape' );
var noop = require( '@stdlib/utils/noop' );
var Sparkline = require( './../lib' );
var set = require( './../lib/set_yvalue.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof set, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a function', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		undefined,
		{},
		[]
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws a type error when provided '+values[i] );
	}
	t.end();
	function badValue( value ) {
		return function badValue() {
			set.call( {}, value );
		};
	}
});

tape( 'the function sets the y-value accessor', function test( t ) {
	var context;
	var v;

	context = new Sparkline();

	set.call( context, noop );
	v = context.yValue;

	t.equal( v, noop, 'sets the value' );

	t.end();
});
