'use strict';

// MODULES //

var tape = require( 'tape' );
var Sparkline = require( './../lib' );
var toString = require( './../lib/tostring.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof toString, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function serializes a sparkline as a string by rendering a chart', function test( t ) {
	var context;
	var str;

	context = new Sparkline();
	context.render = render;

	str = toString.call( context );

	t.equal( str, '▁█▅▃▆▆▅', 'returns a rendered chart' );

	t.end();

	function render() {
		return '▁█▅▃▆▆▅';
	}
});
