'use strict';

/**
* Create a Unicode sparkline column chart.
*
* @module @stdlib/plot/unicode/sparklines/column-chart
*
* @example
* var columnChart = require( '@stdlib/plot/unicode/sparklines/column-chart' );
*
* var data = [ 1.0, 5.0, 3.0, 2.0, 4.0, 4.0, 3.0 ];
* var opts = {
*     'data': data
* };
* var chart = columnChart( opts );
* var str = chart.render();
* // returns '▁█▅▃▆▆▅'
*/

// MODULES //

var columnChart = require( './ctor.js' );


// EXPORTS //

module.exports = columnChart;
