'use strict';

/**
* Create a Unicode sparkline tristate chart.
*
* @module @stdlib/plot/unicode/sparklines/tristate-chart
*
* @example
* var tristate = require( '@stdlib/plot/unicode/sparklines/tristate-chart' );
*
* var data = [ -1, 1, 0, 0, 1, -1, -1, 1 ];
* var opts = {
*     'data': data
* };
* var chart = tristate( opts );
* var str = chart.render();
* // returns '▄▀──▀▄▄▀'
*/

// MODULES //

var tristate = require( './ctor.js' );


// EXPORTS //

module.exports = tristate;
