'use strict';

// MODULES //

var floor = require( '@stdlib/math/base/special/floor' );
var exp10 = require( '@stdlib/math/base/special/exp10' );


// STEM-AND-LEAF //

/**
* Creates a stem-and-leaf plot.
*
* @param {(Array|TypedArray)} x - input data
* @returns {string} stem-and-leaf chart representation
*/
function stemleaf( x, ldigits ) {
	var interval;
	var len = x.length;
	var stm;
	var lf;
	var i;

	interval = exp10( ldigits );
	stm = new Array( len );
	lf = new Array( len );

	for ( i = 0; i < len; i++ ) {
		stm[ i ] = floor( x[ i ] / interval );
		lf[ i ] = x[ i ] % interval;
	}

} // end FUNCTION stemleaf()


// EXPORTS //

module.exports = stemleaf;
