'use strict';

// MAIN //

/**
* Changes the current working directory.
*
* @param {string} path - desired working directory
* @returns {(Error|null)} error object
*
* @example
* var err = chdir( __dirname );
* if ( err ) {
*     throw err;
* }
*/
function chdir( path ) {
	try {
		process.chdir( path );
	} catch ( err ) {
		return err;
	}
	return null;
} // end FUNCTION chdir()


// EXPORTS //

module.exports = chdir;
