'use strict';

// MODULES //

var isFunction = require( '@stdlib/assert/is-function' );
var polyfill = require( './polyfill.js' );
var builtin = require( './native.js' );


// MAIN //

/**
* Returns the numeric group identity of the calling process.
*
* @name getgid
* @type {Function}
* @returns {(integer|null)} numeric group identity or null
*
* @example
* var gid = getgid();
*/
var getgid = ( isFunction( builtin ) ) ? builtin : polyfill;


// EXPORTS //

module.exports = getgid;
