'use strict';

/**
* Read data from `stdin`.
*
* @module @stdlib/process/read-stdin
*
* @example
* var stdin = require( '@stdlib/process/read-stdin' );
*
* function onRead( error, data ) {
*     if ( error ) {
*         throw error;
*     }
*     console.log( data.toString() );
*     // returns '...'
* }
*
* stdin( onRead );
*/

// MODULES //

var stdin = require( './read.js' );


// EXPORTS //

module.exports = stdin;
