'use strict';

// MODULES //

var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isProbability = require( '@stdlib/assert/is-probability' ).isPrimitive;


// MAIN //

/**
* Validates parameters.
*
* @private
* @param {PositiveInteger} n - number of trials
* @param {Probability} p - success probability
* @returns {(Error|null)} error or null
*
* @example
* var err = validate( 10, 0.5 );
* if ( err ) {
*     throw err;
* }
*/
function validate( n, p ) {
	if ( !isPositiveInteger( n ) ) {
		return new TypeError( 'invalid input argument. First argument must be a positive integer. Value: `' + p + '`.' );
	}
	if ( !isProbability( p ) ) {
		return new TypeError( 'invalid input argument. Second argument must be a probability. Value: `' + p + '`.' );
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
