'use strict';

// MODULES //

var isNumber = require( '@stdlib/assert/is-number' ).isPrimitive;
var isPositive = require( '@stdlib/assert/is-positive-number' ).isPrimitive;
var isnan = require( '@stdlib/assert/is-nan' );


// MAIN //

/**
* Validates parameters.
*
* @private
* @param {number} x0 - location parameter
* @param {PositiveNumber} gamma - scale parameter
* @returns {(Error|null)} error or null
*
* @example
* var err = validate( 1.0, 2.5 );
* if ( err ) {
*     throw err;
* }
*/
function validate( x0, gamma ) {
	if ( !isNumber( x0 ) || isnan( x0 ) ) {
		return new TypeError( 'invalid input argument. `x0` must be a number primitive and not `NaN`. Value: `' + x0 + '`.' );
	}
	if ( !isPositive( gamma ) ) {
		return new TypeError( 'invalid input argument. `gamma` must be a positive number. Value: `' + gamma + '`.' );
	}
	return null;
}


// EXPORTS //

module.exports = validate;
