'use strict';

/**
* Chi distributed pseudorandom numbers.
*
* @module @stdlib/random/base/chi
*
* @example
* var chi = require( '@stdlib/random/base/chi' );
*
* var v = chi( 6 );
* // returns <number>
*
* @example
* var factory = require( '@stdlib/random/base/chi' ).factory;
*
* var chi = factory( 3, {
*     'seed': 297
* });
*
* var v = chi();
* // returns 1.5446650738420364
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var chi = require( './chi.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( chi, 'factory', factory );


// EXPORTS //

module.exports = chi;
