'use strict';

// MODULES //

var defineProperty = require( '@stdlib/utils/define-property' );
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var isObject = require( '@stdlib/assert/is-plain-object' );
var randu = require( '@stdlib/random/base/mt19937' ).factory;
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var validate = require( './validate.js' );
var cosine0 = require( './cosine.js' );


// MAIN //

/**
* Returns a pseudorandom number generator for generating random numbers from a raised cosine distribution.
*
* @param {number} [mu] - mean parameter
* @param {PositiveNumber} [s] - scale parameter
* @param {Options} [options] - function options
* @param {*} [options.seed] - pseudorandom number generator seed
* @throws {TypeError} `mu` must be a number
* @throws {TypeError} `s` must be a positive number
* @throws {TypeError} options argument must be an object
* @returns {Function} pseudorandom number generator
*
* @example
* var cosine = factory( 0.0, 1.0 );
* var v = cosine();
* // returns <number>
*
* @example
* var cosine = factory( -3.0, 0.5, {
*     'seed': 297
* });
* var v = cosine();
* // returns <number>
*/
function factory() {
	var opts;
	var rand;
	var prng;
	var err;
	var mu;
	var s;
	if ( arguments.length === 0 ) {
		rand = randu();
	} else if ( arguments.length === 1 ) {
		opts = arguments[ 0 ];
		if ( !isObject( opts ) ) {
			throw new TypeError( 'invalid argument. Options argument must be an object. Value: `' + opts + '`.' );
		}
		rand = randu( opts );
	} else {
		mu = arguments[ 0 ];
		s = arguments[ 1 ];
		err = validate( mu, s );
		if ( err ) {
			throw err;
		}
		if ( arguments.length > 2 ) {
			opts = arguments[ 2 ];
			if ( !isObject( opts ) ) {
				throw new TypeError( 'invalid argument. Options argument must be an object. Value: `' + opts + '`.' );
			}
			rand = randu( opts );
		} else {
			rand = randu();
		}
	}
	rand = rand.normalized;

	if ( mu === void 0 ) {
		prng = cosine2;
	} else {
		prng = cosine1;
	}
	setReadOnly( prng, 'NAME', 'cosine' );
	setReadOnly( prng, 'SEED', rand.SEED );
	defineProperty( prng, 'STATE', {
		'configurable': false,
		'enumerable': true,
		'get': getState,
		'set': setState
	});
	setReadOnly( prng, 'STATE_LENGTH', rand.STATE_LENGTH );
	setReadOnly( prng, 'STATE_SIZE', rand.STATE_SIZE );
	setReadOnly( prng, 'PRNG', rand );

	return prng;

	/**
	* Returns the current pseudorandom number generator state.
	*
	* @private
	* @returns {Uint32Array} current state
	*/
	function getState() {
		return rand.STATE;
	}

	/**
	* Sets the pseudorandom number generator state.
	*
	* @private
	* @param {Uint32Array} s - generator state
	*/
	function setState( s ) {
		rand.STATE = s;
	}

	/**
	* Returns a pseudorandom number drawn from a raised cosine distribution with bound parameters.
	*
	* @private
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = cosine1();
	* // returns <number>
	*/
	function cosine1() {
		return cosine0( rand, mu, s );
	}

	/**
	* Returns a pseudorandom number drawn from a raised cosine distribution.
	*
	* @private
	* @param {number} mu - mean
	* @param {PositiveNumber} s - scale parameter
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = cosine2( 0.0, 2.0 );
	* // returns <number>
	*
	* @example
	* var v = cosine2( 0.0, -1.0 );
	* // returns NaN
	*/
	function cosine2( mu, s ) {
		if (
			isnan( mu ) ||
			isnan( s ) ||
			s <= 0.0
		) {
			return NaN;
		}
		return cosine0( rand, mu, s );
	}
}


// EXPORTS //

module.exports = factory;
