'use strict';

// MODULES //

var isPositive = require( '@stdlib/assert/is-positive-number' ).isPrimitive;
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;


// MAIN //

/**
* Validates parameters.
*
* @private
* @param {PositiveInteger} k - shape parameter
* @param {PositiveNumber} lambda - rate parameter
* @returns {(Error|null)} error or null
*
* @example
* var err = validate( 1, 2.5 );
* if ( err ) {
*     throw err;
* }
*/
function validate( k, lambda ) {
	if ( !isPositiveInteger( k ) ) {
		return new TypeError( 'invalid input argument. First argument must be a positive integer. Value: `' + k + '`.' );
	}
	if ( !isPositive( lambda ) ) {
		return new TypeError( 'invalid input argument. Second argument must be a positive number. Value: `' + lambda + '`.' );
	}
	return null;
}


// EXPORTS //

module.exports = validate;
