'use strict';

// MODULES //

var defineProperty = require( '@stdlib/utils/define-property' );
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var isObject = require( '@stdlib/assert/is-plain-object' );
var isBoolean = require( '@stdlib/assert/is-boolean' ).isPrimitive;
var isCollection = require( '@stdlib/assert/is-collection' );
var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isInt32Array = require( '@stdlib/assert/is-int32array' );
var gcopy = require( '@stdlib/blas/base/gcopy' );
var floor = require( '@stdlib/math/base/special/floor' );
var Int32Array = require( '@stdlib/array/int32' );
var INT32_MAX = require( '@stdlib/constants/math/int32-max' );
var createTable = require( './create_table.js' );
var randint32 = require( './rand_int32.js' );


// VARIABLES //

var NORMALIZATION_CONSTANT = (INT32_MAX - 1)|0; // asm type annotation
var MAX_SEED = (INT32_MAX - 1)|0; // asm type annotation
var TABLE_SIZE = 32;
var STATE_SIZE = TABLE_SIZE + 2;
var SHUFFLE_STATE = STATE_SIZE - 1; // index
var PRNG_STATE = STATE_SIZE - 2; // index
var A = 16807|0; // asm type annotation


// MAIN //

/**
* Returns a linear congruential pseudorandom number generator (LCG) whose output is shuffled.
*
* @param {Options} [options] - options
* @param {(integer32|Collection<integer32>)} [options.seed] - pseudorandom number generator seed
* @param {Int32Array} [options.state] - pseudorandom number generator state
* @param {boolean} [options.copy=true] - boolean indicating whether to copy a provided pseudorandom number generator state
* @throws {TypeError} options argument must be an object
* @throws {TypeError} a seed must be either a positive integer less than the maximum signed 32-bit integer or an array-like object containing integers less than the maximum signed 32-bit integer
* @throws {RangeError} a numeric seed must be a positive integer less than the maximum signed 32-bit integer
* @throws {TypeError} state must be an `Int32Array`
* @throws {RangeError} insufficient state length
* @throws {TypeError} `copy` option must be a boolean
* @returns {Function} shuffled LCG PRNG
*
* @example
* var minstd = factory();
*
* var v = minstd();
* // returns <number>
*
* @example
* // Return a seeded LCG:
* var minstd = factory({
*     'seed': 1234
* });
*
* var v = minstd();
* // returns 1421600654
*/
function factory( options ) {
	var state;
	var seed;
	var slen;
	if ( arguments.length ) {
		if ( !isObject( options ) ) {
			throw new TypeError( 'invalid argument. Options argument must be an object. Value: `' + options + '`.' );
		}
		if ( hasOwnProp( options, 'copy' ) ) {
			if ( !isBoolean( options.copy ) ) {
				throw new TypeError( 'invalid option. `copy` option must be a boolean. Option: `' + options.copy + '`.' );
			}
		}
		if ( hasOwnProp( options, 'state' ) ) {
			state = options.state;
			if ( !isInt32Array( state ) ) {
				throw new TypeError( 'invalid option. `state` option must be an Int32Array. Option: `' + state + '`.' );
			}
			// Determine the seed length:
			slen = state.length - STATE_SIZE;
			if ( slen <= 0 ) {
				throw new RangeError( 'invalid option. `state` option has insufficient length.' );
			}
			if ( options.copy === false ) {
				// Create a state "view":
				state = new Int32Array( state.buffer, state.byteOffset, STATE_SIZE ); // eslint-disable-line max-len

				// Create a seed "view":
				seed = new Int32Array( state.buffer, state.byteOffset+(STATE_SIZE*state.BYTES_PER_ELEMENT), slen ); // eslint-disable-line max-len
			} else {
				// Extract the state:
				state = gcopy( STATE_SIZE, state, 1, new Int32Array( STATE_SIZE ), 1 ); // eslint-disable-line max-len

				// Extract the PRNG seed:
				seed = gcopy.ndarray( slen, state, 1, STATE_SIZE, new Int32Array( slen ), 1, 0 ); // eslint-disable-line max-len
			}
		}
		// If provided a PRNG state, we ignore the `seed` option...
		if ( seed === void 0 ) {
			if ( hasOwnProp( options, 'seed' ) ) {
				seed = options.seed;
				if ( isPositiveInteger( seed ) ) {
					if ( seed > MAX_SEED ) {
						throw new RangeError( 'invalid option. `seed` option must be a positive integer less than the maximum signed 32-bit integer. Option: `' + seed + '`.' );
					}
					seed |= 0; // asm type annotation
				} else if ( isCollection( seed ) && seed.length > 0 ) {
					slen = seed.length;

					// Create an internal copy of the provided seed array to prevent the inability to reproduce PRNG values based on the PRNG's stated seed due to external state mutation:
					seed = gcopy( slen, seed, 1, new Int32Array( slen ), 1 );

					// Initialize the internal PRNG state:
					state = new Int32Array( STATE_SIZE );
					state[ PRNG_STATE ] = seed[ 0 ];
					state = createTable( minstd, state, TABLE_SIZE );
					state[ SHUFFLE_STATE ] = state[ 0 ];
				} else {
					throw new TypeError( 'invalid option. `seed` option must be either a positive integer less than the maximum signed 32-bit integer or an array-like object containing integer values less than the maximum signed 32-bit integer. Option: `' + seed + '`.' );
				}
			} else {
				seed = randint32()|0; // asm type annotation
			}
		}
	} else {
		seed = randint32()|0; // asm type annotation
	}
	if ( state === void 0 ) {
		state = new Int32Array( STATE_SIZE );
		state[ PRNG_STATE ] = seed;
		state = createTable( minstd, state, TABLE_SIZE );
		state[ SHUFFLE_STATE ] = state[ 0 ];

		seed = new Int32Array( [ seed ] );
		slen = 1;
	}
	setReadOnly( minstdShuffle, 'NAME', 'minstd-shuffle' );
	defineProperty( minstdShuffle, 'SEED', {
		'configurable': false,
		'enumerable': true,
		'get': getSeed
	});
	defineProperty( minstdShuffle, 'STATE', {
		'configurable': false,
		'enumerable': true,
		'get': getState,
		'set': setState
	});
	defineProperty( minstdShuffle, 'STATE_LENGTH', {
		'configurable': false,
		'enumerable': true,
		'get': getStateLength
	});
	defineProperty( minstdShuffle, 'STATE_SIZE', {
		'configurable': false,
		'enumerable': true,
		'get': getStateSize
	});
	setReadOnly( minstdShuffle, 'MIN', 1 );
	setReadOnly( minstdShuffle, 'MAX', INT32_MAX-1 );
	setReadOnly( minstdShuffle, 'normalized', normalized );

	setReadOnly( normalized, 'NAME', minstdShuffle.NAME );
	defineProperty( normalized, 'SEED', {
		'configurable': false,
		'enumerable': true,
		'get': getSeed
	});
	defineProperty( normalized, 'STATE', {
		'configurable': false,
		'enumerable': true,
		'get': getState,
		'set': setState
	});
	defineProperty( normalized, 'STATE_LENGTH', {
		'configurable': false,
		'enumerable': true,
		'get': getStateLength
	});
	defineProperty( normalized, 'STATE_SIZE', {
		'configurable': false,
		'enumerable': true,
		'get': getStateSize
	});
	setReadOnly( normalized, 'MIN', (minstdShuffle.MIN-1) / NORMALIZATION_CONSTANT );
	setReadOnly( normalized, 'MAX', (minstdShuffle.MAX-1) / NORMALIZATION_CONSTANT );

	return minstdShuffle;

	/**
	* Returns the generator seed.
	*
	* @private
	* @returns {Int32Array} seed
	*/
	function getSeed() {
		return gcopy( slen, seed, 1, new Int32Array( slen ), 1 );
	}

	/**
	* Returns the current pseudorandom number generate state length.
	*
	* @private
	* @returns {PositiveInteger} state length
	*/
	function getStateLength() {
		return STATE_SIZE + slen;
	}

	/**
	* Returns the current pseudorandom number generate state size (in bytes).
	*
	* @private
	* @returns {PositiveInteger} state size (in bytes)
	*/
	function getStateSize() {
		return getStateLength() * 4; // 4 bytes per int32
	}

	/**
	* Returns the current pseudorandom number generator state.
	*
	* @private
	* @returns {Int32Array} current state
	*/
	function getState() {
		var out = new Int32Array( getStateLength() );

		// Copy the internal state:
		out = gcopy( STATE_SIZE, state, 1, out, 1 );

		// Copy the seed:
		out = gcopy.ndarray( slen, seed, 1, 0, out, 1, STATE_SIZE );

		return out;
	}

	/**
	* Sets the pseudorandom number generator state.
	*
	* @private
	* @param {Int32Array} s - generator state
	* @throws {TypeError} must provide an `Int32Array`
	* @throws {RangeError} must provide an `Int32Array` having an expected length
	*/
	function setState( s ) {
		if ( !isInt32Array( s ) ) {
			throw new TypeError( 'invalid argument. Must provide an Int32Array. Value: `' + s + '`.' );
		}
		// Determine the seed length:
		slen = s.length - STATE_SIZE;
		if ( slen <= 0 ) {
			throw new RangeError( 'invalid argument. The provided array has insufficient length.' );
		}
		// Update the internal state:
		gcopy( STATE_SIZE, s, 1, state, 1 );

		// Set the seed:
		seed = gcopy.ndarray( slen, s, 1, STATE_SIZE, new Int32Array( slen ), 1, 0 ); // eslint-disable-line max-len
	}

	/**
	* Generates a pseudorandom integer on the interval \\( [1,2^{31}-1) \\).
	*
	* @private
	* @returns {integer32} pseudorandom integer
	*/
	function minstd() {
		var s = state[ PRNG_STATE ]|0; // asm type annotation
		s = ( (A*s)%INT32_MAX )|0; // asm type annotation
		state[ PRNG_STATE ] = s;
		return s|0; // asm type annotation
	}

	/**
	* Generates a pseudorandom integer on the interval \\( [1,2^{31}-1) \\).
	*
	* @private
	* @returns {integer32} pseudorandom integer
	*
	* @example
	* var v = minstd();
	* // returns <number>
	*/
	function minstdShuffle() {
		var s;
		var i;

		s = state[ SHUFFLE_STATE ];
		i = floor( TABLE_SIZE * (s/INT32_MAX) );

		// Pull a state from the table:
		s = state[ i ];

		// Update the PRNG state:
		state[ SHUFFLE_STATE ] = s;

		// Replace the pulled state:
		state[ i ] = minstd();

		return s;
	}

	/**
	* Generates a pseudorandom number on the interval \\( [0,1) \\).
	*
	* @private
	* @returns {number} pseudorandom number
	*
	* @example
	* var v = normalized();
	* // returns <number>
	*/
	function normalized() {
		return (minstdShuffle()-1) / NORMALIZATION_CONSTANT;
	}
}


// EXPORTS //

module.exports = factory;
