#include <stdlib.h>
#include <stdio.h>
#include "stdlib/random/base.h"
#include "stdlib/random/base/minstd.h"

int main() {
	// Create a PRNG...
	struct BasePRNGObject *obj = stdlib_base_minstd_prng_allocate( 12345 );
	if ( obj == NULL ) {
		fprintf( stderr, "Error allocating memory.\n" );
		exit( 1 );
	}

	printf( "name = %s\n", obj->prng->name );
	printf( "min = %llu\n", obj->prng->min );
	printf( "max = %llu\n", obj->prng->max );

	stdlib_base_minstd_prng_free( obj );
}
