'use strict';

var rayleigh = require( './../lib' );

var seed;
var rand;
var i;

// Generate pseudorandom numbers...
console.log( '\nseed: %d', rayleigh.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rayleigh( 1.0 ) );
}

// Create a new pseudorandom number generator...
seed = 1234;
rand = rayleigh.factory( 5.0, {
	'seed': seed
});
console.log( '\nseed: %d', seed );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}

// Create another pseudorandom number generator using a previous seed...
rand = rayleigh.factory( 1.0, {
	'seed': rayleigh.SEED
});
console.log( '\nseed: %d', rayleigh.SEED );
for ( i = 0; i < 100; i++ ) {
	console.log( rand() );
}
