'use strict';

// MODULES //

var tape = require( 'tape' );
var RE_BASENAME_POSIX = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( RE_BASENAME_POSIX instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'the regular expression captures the last part of a POSIX path (basename)', function test( t ) {
	var expected;
	var values;
	var base;
	var i;

	values = [
		'index.js',
		'/foo/bar/home.html',
		'/foo/bar/home',
		'foo/file.pdf',
		'./foo/file',
		'./index.js/',
		'foo/bar/home.html/',
		'.gitigno.re',
		'/foo/bar/.editorconf.ig',
		'main/.travis.yml',
		'./foo foo foo/bar bar_bar/',
		'./../../foo/bar',
		'./../../',
		'./',
		'.',
		'boop.'
	];

	expected = [
		'index.js',
		'home.html',
		'home',
		'file.pdf',
		'file',
		'index.js',
		'home.html',
		'.gitigno.re',
		'.editorconf.ig',
		'.travis.yml',
		'bar bar_bar',
		'bar',
		'..',
		'.',
		'.',
		'boop.'
	];

	for ( i = 0; i < values.length; i++ ) {
		base = RE_BASENAME_POSIX.exec( values[ i ] )[ 1 ];
		t.strictEqual( base, expected[ i ], values[ i ] );
	}
	t.end();
});
