'use strict';

// MODULES //

var tape = require( 'tape' );
var RE_BASENAME_WINDOWS = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( RE_BASENAME_WINDOWS instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'the regular expression captures the last part of a Windows path', function test( t ) {
	var expected;
	var values;
	var base;
	var i;

	values = [
		'index.js',
		'C:\\foo\\bar\\home.html',
		'\\foo\\bar\\',
		'foo\\file.pdf',
		'foo\\file',
		'index.js\\',
		'foo\\bar\\home.html\\',
		'foo\\..\\..\\bar\\home.html',
		'.gitigno.re',
		'C:\\foo\\bar\\.editorconf.ig',
		'main\\.travis.yml',
		'boop.',
		'.',
		''
	];

	expected = [
		'index.js',
		'home.html',
		'bar',
		'file.pdf',
		'file',
		'index.js',
		'home.html',
		'home.html',
		'.gitigno.re',
		'.editorconf.ig',
		'.travis.yml',
		'boop.',
		'.',
		''
	];

	for ( i = 0; i < values.length; i++ ) {
		base = RE_BASENAME_WINDOWS.exec( values[ i ] )[ 1 ];
		t.strictEqual( base, expected[ i ], values[ i ] );
	}
	t.end();
});
