'use strict';

var RE_BASENAME = require( './../lib' );

// Assuming a POSIX platform...
var base = RE_BASENAME.exec( '/foo/bar/index.js' )[ 1 ];
console.log( base );
// => 'index.js'

base = RE_BASENAME.posix.exec( '/foo/bar/home.html' )[ 1 ];
console.log( base );
// => 'home.html'

base = RE_BASENAME.win32.exec( 'C:\\foo\\bar\\home.html' )[ 1 ];
console.log( base );
// => 'home.html'
