'use strict';

/**
* Regular expression to capture a decimal number.
*
* @module @stdlib/regexp/decimal-number
*
* @example
* var RE_DECIMAL_NUMBER = require( '@stdlib/regexp/decimal-number' );
*
* var bool = RE_DECIMAL_NUMBER.test( 'beep 1.0 boop' );
* // returns true
*
* bool = RE_DECIMAL_NUMBER.test( 'beep 1 boop' );
* // returns false
*/

// MAIN //

/**
* Captures a decimal number.
*
* Regular expression: `/([-+]{0,1}[0-9]*\.[0-9]+)/
*
* -   `()`
*     -   capture
*
* -   `[-+]{0,1}`
*     -   match a minus or plus sign zero or one time
*
* -   `[0-9]*`
*     -   match the characters `[0-9]` zero or more times
*
* -   `\.`
*     -   match a `.` literal
*
* -   `[0-9]+`
*     -   match the characters `[0-9]` one or more times
*
*
* @constant
* @type {RegExp}
* @default /([-+]{0,1}[0-9]*\.[0-9]+)/
*/
var RE_DECIMAL_NUMBER = /([-+]{0,1}[0-9]*\.[0-9]+)/;


// EXPORTS //

module.exports = RE_DECIMAL_NUMBER;
