var dir;

dir = RE_DIRNAME_POSIX.exec( 'index.js' )[ 1 ];
console.log( dir );
// returns ''

dir = RE_DIRNAME_POSIX.exec( '.' )[ 1 ];
console.log( dir );
// returns '.'

dir = RE_DIRNAME_POSIX.exec( './' )[ 1 ];
