'use strict';

// MODULES //

var tape = require( 'tape' );
var RE = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.equal( RE instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'the regular expression captures Windows path dirnames', function test( t ) {
	var expected;
	var values;
	var dir;
	var i;

	values = [
		'index.js',
		'C:\\foo\\bar\\home.html',
		'\\foo\\bar\\',
		'foo\\file.pdf',
		'foo\\file',
		'index.js\\',
		'foo\\bar\\home.html\\',
		'foo\\..\\..\\bar\\home.html',
		'.gitigno.re',
		'C:\\foo\\bar\\.editorconf.ig',
		'main\\.travis.yml',
		'boop.'
	];

	expected = [
		'',
		'C:\\foo\\bar',
		'\\foo',
		'foo',
		'foo',
		'',
		'foo\\bar',
		'foo\\..\\..\\bar',
		'',
		'C:\\foo\\bar',
		'main',
		''
	];

	for ( i = 0; i < values.length; i++ ) {
		dir = RE.exec( values[ i ] )[ 1 ];
		t.equal( dir, expected[ i ], values[ i ] );
	}
	t.end();
});
