/* tslint:disable:no-unused-expression */

import regexp = require( './index' );


// TESTS //

// The exported object has the expected function interfaces...
{
	regexp.reBasename; // $ExpectType ReBasename
	regexp.reBasenamePosix; // $ExpectType ReBasenamePosix
	regexp.reBasenameWindows; // $ExpectType ReBasenameWindows
	regexp.reColorHexadecimal; // $ExpectType ReColorHexadecimal
	regexp.reDecimalNumber; // $ExpectType ReDecimalNumber
	regexp.reDirname; // $ExpectType ReDirname
	regexp.reDirnamePosix; // $ExpectType ReDirnamePosix
	regexp.reDirnameWindows; // $ExpectType ReDirnameWindows
	regexp.reEOL; // $ExpectType ReEOL
	regexp.reExtendedLengthPath; // $ExpectType ReExtendedLengthPath
	regexp.reExtname; // $ExpectType ReExtname
	regexp.reExtnamePosix; // $ExpectType ReExtnamePosix
	regexp.reExtnameWindows; // $ExpectType ReExtnameWindows
	regexp.reFilename; // $ExpectType ReFilename
	regexp.reFilenamePosix; // $ExpectType ReFilenamePosix
	regexp.reFilenameWindows; // $ExpectType ReFilenameWindows
	regexp.reFunctionName; // $ExpectType ReFunctionName
	regexp.reNativeFunction; // $ExpectType ReNativeFunction
	regexp.reRegExp; // $ExpectType ReNativeFunction
	regexp.reUncPath; // $ExpectType ReUncPath
	regexp.reUtf16SurrogatePair; // $ExpectType ReUtf16SurrogatePair
	regexp.reUtf16UnpairedSurrogate; // $ExpectType ReUtf16UnpairedSurrogate
	regexp.reWhitespace; // $ExpectType ReWhitespace
}
