'use strict';

var RE_EOL = require( './../lib' );

var str;

console.log( RE_EOL.test( '\r\n' ) );
// => true

console.log( RE_EOL.test( '\n' ) );
// => true

console.log( RE_EOL.test( '\r' ) );
// => false

console.log( RE_EOL.test( '\\r\\n' ) );
// => false

console.log( RE_EOL.test( 'beep' ) );
// => false

str = 'This is\na newline\r\ndelimited string.';
console.log( str.split( RE_EOL ) );
