'use strict';

// MODULES //

var tape = require( 'tape' );
var RE = require( './../lib' );


// TESTS //

tape( 'main export is a regular expression', function test( t ) {
	t.ok( true, __filename );
	t.equal( RE instanceof RegExp, true, 'main export is a regular expression' );
	t.end();
});

tape( 'the regular expression captures POSIX filename extensions', function test( t ) {
	var expected;
	var values;
	var ext;
	var i;

	values = [
		'index.js',
		'/foo/bar/home.html',
		'foo/file.pdf',
		'index.js/',
		'foo/bar/home.html/',
		'.gitigno.re',
		'/foo/bar/.editorconf.ig',
		'main/.travis.yml',
		'boop.'
	];

	expected = [
		'.js',
		'.html',
		'.pdf',
		'.js',
		'.html',
		'.re',
		'.ig',
		'.yml',
		'.'
	];

	for ( i = 0; i < values.length; i++ ) {
		ext = RE.exec( values[ i ] )[ 1 ];
		t.equal( ext, expected[ i ], values[ i ] );
	}
	t.end();
});

tape( 'the regular eexpression does not capture anything if provided a path not having a filename extension, including dotfiles not having an extension', function test( t ) {
	var values;
	var ext;
	var i;

	values = [
		'',
		'/foo/bar/file',
		'/foo/bar/.gitignore',
		'.editorconfig',
		'.jshintrc',
		'jshintignore',
		'beep',
		'/foo/bar/file/'
	];

	for ( i = 0; i < values.length; i++ ) {
		ext = RE.exec( values[ i ] )[ 1 ];
		t.equal( ext, '', values[ i ] );
	}
	t.end();
});
