'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var IS_WINDOWS = require( '@stdlib/utils/is-windows' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var pkg = require( './../package.json' ).name;
var RE_EXTNAME = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var out;
	var str;
	var i;

	if ( IS_WINDOWS ) {
		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			str = 'C:\\foo\\bar\\beep\\boop'+String.fromCharCode( 97 + (i%26) )+'.js';
			out = RE_EXTNAME.exec( str );
			if ( !out || !isString( out[ 1 ] ) ) {
				b.fail( 'should capture an extname' );
			}
		}
		b.toc();
	} else {
		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			str = '/foo/bar/beep/boop'+String.fromCharCode( 97 + (i%26) )+'.js';
			out = RE_EXTNAME.exec( str );
			if ( !out || !isString( out[ 1 ] ) ) {
				b.fail( 'should capture an extname' );
			}
		}
		b.toc();
	}
	if ( !out || !isString( out[ 1 ] ) ) {
		b.fail( 'should capture an extname' );
	} else {
		b.pass( 'benchmark finished' );
	}
	b.end();
});

