'use strict';

var RE_EXTNAME = require( './../lib' );

var ext;

// Assuming a POSIX platform...
ext = RE_EXTNAME.exec( '/foo/bar/index.js' )[ 1 ];
console.log( ext );
// => '.js'

ext = RE_EXTNAME.posix.exec( '/foo/bar/home.html' )[ 1 ];
console.log( ext );
// => '.html'

ext = RE_EXTNAME.win32.exec( 'C:\\foo\\bar\\home.html' )[ 1 ];
console.log( ext );
// => '.html'
