'use strict';

var RE_FILENAME = require( './../lib' );

// Assuming a POSIX platform...
var parts = RE_FILENAME.exec( '/foo/bar/index.js' );
console.log( parts.slice() );
/* =>
	[
		'/foo/bar/index.js',
		'/',
		'foo/bar/',
		'index.js',
		'.js'
	]
*/

parts = RE_FILENAME.posix.exec( '/foo/bar/home.html' );
console.log( parts.slice() );
/* =>
	[
		'/foo/bar/home.html',
		'/',
		'foo/bar/',
		'home.html',
		'.html'
	]
*/

parts = RE_FILENAME.win32.exec( 'C:\\foo\\bar\\home.html' );
console.log( parts.slice() );
/* =>
	[
		'C:\\foo\\bar\\home.html',
		'C:',
		'\\',
		'foo\\bar\\',
		'home.html',
		'.html'
	]
*/
