'use strict';

/**
* Regular expression to split a filename.
*
* @module @stdlib/regexp/filename
* @type {RegExp}
*
* @example
* var RE_FILENAME = require( '@stdlib/regexp/filename' );
*
* // On a POSIX platform...
* var parts = RE_FILENAME.exec( '/foo/bar/index.js' ).slice();
* // returns [ '/foo/bar/index.js', '/', 'foo/bar/', 'index.js', '.js' ]
*
* @example
* var RE_FILENAME = require( '@stdlib/regexp/filename' );
*
* // On a Windows platform...
* var parts = RE_FILENAME.exec( 'C:\\foo\\bar\\index.js' ).slice();
* // returns [ 'C:\\foo\\bar\\index.js', 'C:', '\\', 'foo\\bar\\', 'index.js', '.js' ]
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var copy = require( '@stdlib/utils/copy' );
var IS_WINDOWS = require( '@stdlib/assert/is-windows' );


// MAIN //

var posix = require( '@stdlib/regexp/filename-posix' );
var win32 = require( '@stdlib/regexp/filename-windows' );

var RE_FILENAME;
if ( IS_WINDOWS ) {
	RE_FILENAME = win32;
} else {
	RE_FILENAME = posix;
}
RE_FILENAME = copy( RE_FILENAME );

setReadOnly( RE_FILENAME, 'posix', posix );
setReadOnly( RE_FILENAME, 'win32', win32 );


// EXPORTS //

module.exports = RE_FILENAME;
