'use strict';

var RE_REGEXP = require( './../lib' );

console.log( RE_REGEXP.test( '/beep/' ) );
// => true

console.log( RE_REGEXP.test( '/^.*$/ig' ) );
// => true

console.log( RE_REGEXP.test( '/^\\/([^\\/]+)\\/(.*)$/' ) );
// => true

console.log( RE_REGEXP.test( '/^\/([^\/]+)\/(.*)$/' ) );
// => false

console.log( RE_REGEXP.test( '/boop' ) );
// => false

console.log( RE_REGEXP.test( '' ) );
// => false
